mapper.select:
SELECT CC.집합자원ID ,CC.중개사업자ID ,CC.집합자원그룹명 ,CC.집합자원구분 ,CC.운영상태 ,CC.신청유형 ,CC.처리상태 ,CC.구성수 ,CC.자원용량 ,CC.PVWT용량 ,CC.ESS용량 ,CC.평균오차율 ,CC.오차율평가 ,CC.거래가능기간 ,CC.신청일자 FROM(SELECT A.id AS 집합자원ID ,A.agency_id AS 중개사업자ID ,A.name AS 집합자원그룹명 ,A.type AS 집합자원구분 ,A.status AS 운영상태 ,A.apply_type AS 신청유형 ,A.apply_status AS 처리상태 ,B.resource_cnt AS 구성수 ,B.capacity AS 자원용량 ,B.capacityPvWt AS PVWT용량 ,B.ess_capacity AS ESS용량 ,C.error_rate AS 평균오차율 ,'' AS 오차율평가 ,'' AS 거래가능기간 ,A.created_at AS 신청일자 FROM resource_set A LEFT OUTER JOIN ( SELECT AA.resource_set_id ,COUNT(resource_id) AS resource_cnt ,(SUM(BB.capacity)+SUM(ess_capacity)) AS capacity ,CASE WHEN BB.facility_type ='01' OR BB.facility_type = '02' THEN SUM(BB.capacity) END AS capacityPvWt ,SUM(BB.ess_capacity) AS ess_capacity FROM resource_set_list AA LEFT OUTER JOIN resource BB ON AA.resource_id = BB.id GROUP BY AA.resource_set_id) B ON A.id = B.resource_set_id LEFT OUTER JOIN forecast_by_resource_set C ON A.id = C.resource_set_id ) CC WHERE 1=1 AND CC.중개사업자ID = #{중개사업자ID}
AND CC.집합자원ID = #{id}
AND CC.집합자원구분 = #{type}
AND CC.운영상태 = #{status}
AND CC.집합자원그룹명 LIKE CONCAT('%',#{name},'%')
AND CC.신청유형 = #{apply_type}
AND CC.처리상태 = #{apply_status}
AND CC.오차율평가 = #{error_rate_cd}
ORDER BY CC.집합자원ID LIMIT #{페이지번호},#{행갯수}

SELECT COUNT(*) AS cnt FROM(SELECT A.id AS 집합자원ID ,A.agency_id AS 중개사업자ID ,A.name AS 집합자원그룹명 ,A.type AS 집합자원구분 ,A.status AS 운영상태 ,A.apply_type AS 신청유형 ,A.apply_status AS 처리상태 ,B.resource_cnt AS 구성수 ,B.capacity AS 자원용량 ,B.capacityPvWt AS PVWT용량 ,B.ess_capacity AS ESS용량 ,C.error_rate AS 평균오차율 ,'' AS 오차율평가 ,'' AS 거래가능기간 ,A.created_at AS 신청일자 FROM resource_set A LEFT OUTER JOIN ( SELECT AA.resource_set_id ,COUNT(resource_id) AS resource_cnt ,(SUM(BB.capacity)+SUM(ess_capacity)) AS capacity ,CASE WHEN BB.facility_type ='01' OR BB.facility_type = '02' THEN SUM(BB.capacity) END AS capacityPvWt ,SUM(BB.ess_capacity) AS ess_capacity FROM resource_set_list AA LEFT OUTER JOIN resource BB ON AA.resource_id = BB.id GROUP BY AA.resource_set_id) B ON A.id = B.resource_set_id LEFT OUTER JOIN forecast_by_resource_set C ON A.id = C.resource_set_id ) CC WHERE 1=1 AND CC.중개사업자ID = #{중개사업자ID}
AND CC.집합자원ID = #{id}
AND CC.집합자원구분 = #{type}
AND CC.운영상태 = #{status}
AND CC.집합자원그룹명 LIKE CONCAT('%',#{name},'%')
AND CC.신청유형 = #{apply_type}
AND CC.처리상태 = #{apply_status}
AND CC.오차율평가 = #{error_rate_cd}

mapper.update:
   UPDATE event SET
       approved_memo = #{승인자메모}
WHERE id = #{이벤트ID}
