mapper.select:
SELECT CC.집합자원ID ,CC.중개사업자ID ,CC.집합자원그룹명 ,CC.집합자원구분 ,CC.운영상태 ,CC.신청유형 ,CC.처리상태 ,CC.구성수 ,CC.자원용량 ,CC.PVWT용량 ,CC.ESS용량 ,CC.평균오차율 ,CC.오차율평가 ,CC.거래가능기간 ,CC.신청일자 FROM(SELECT A.id AS 집합자원ID ,A.agency_id AS 중개사업자ID ,A.name AS 집합자원그룹명 ,A.type AS 집합자원구분 ,A.status AS 운영상태 ,A.apply_type AS 신청유형 ,A.apply_status AS 처리상태 ,B.resource_cnt AS 구성수 ,B.capacity AS 자원용량 ,B.capacityPvWt AS PVWT용량 ,B.ess_capacity AS ESS용량 ,C.error_rate AS 평균오차율 ,(CASE WHEN C.error_rate >= 0 AND C.error_rate <= 6 THEN '좋음' WHEN C.error_rate > 6 AND C.error_rate <= 8 THEN '보통' WHEN C.error_rate > 8 THEN '나쁨' END) AS 오차율평가 ,'' AS 거래가능기간 ,A.created_at AS 신청일자 FROM resource_set A LEFT OUTER JOIN ( SELECT AA.resource_set_id ,COUNT(resource_id) AS resource_cnt ,((CASE WHEN BB.facility_type ='01' OR BB.facility_type = '02' THEN SUM(BB.capacity) ELSE 0 END)+SUM(ess_capacity)) AS capacity ,CASE WHEN BB.facility_type ='01' OR BB.facility_type = '02' THEN SUM(BB.capacity) END AS capacityPvWt ,SUM(BB.ess_capacity) AS ess_capacity FROM resource_set_list AA LEFT OUTER JOIN resource BB ON AA.resource_id = BB.id GROUP BY AA.resource_set_id) B ON A.id = B.resource_set_id LEFT OUTER JOIN forecast_by_resource_set C ON A.id = C.resource_set_id ) CC WHERE 1=1 AND CC.중개사업자ID = #{중개사업자ID}
AND CC.집합자원ID = #{id}
AND CC.집합자원구분 = #{type}
AND CC.운영상태 = #{status}
AND CC.집합자원그룹명 LIKE CONCAT('%',#{name},'%')
AND CC.신청유형 = #{apply_type}
AND CC.처리상태 = #{apply_status}
AND CC.오차율평가 >= 0 AND CC.오차율평가 <= 6
AND CC.오차율평가 > 6 AND CC.오차율평가 <= 8
AND CC.오차율평가 > 8
ORDER BY CC.집합자원ID LIMIT #{페이지번호},#{행갯수}

SELECT COUNT(*) AS cnt FROM(SELECT A.id AS 집합자원ID ,A.agency_id AS 중개사업자ID ,A.name AS 집합자원그룹명 ,A.type AS 집합자원구분 ,A.status AS 운영상태 ,A.apply_type AS 신청유형 ,A.apply_status AS 처리상태 ,B.resource_cnt AS 구성수 ,B.capacity AS 자원용량 ,B.capacityPvWt AS PVWT용량 ,B.ess_capacity AS ESS용량 ,C.error_rate AS 평균오차율 ,(CASE WHEN C.error_rate >= 0 AND C.error_rate <= 6 THEN '좋음' WHEN C.error_rate > 6 AND C.error_rate <= 8 THEN '보통' WHEN C.error_rate > 8 THEN '나쁨' END) AS 오차율평가 ,'' AS 거래가능기간 ,A.created_at AS 신청일자 FROM resource_set A LEFT OUTER JOIN ( SELECT AA.resource_set_id ,COUNT(resource_id) AS resource_cnt ,((CASE WHEN BB.facility_type ='01' OR BB.facility_type = '02' THEN SUM(BB.capacity) ELSE 0 END)+SUM(ess_capacity)) AS capacity ,CASE WHEN BB.facility_type ='01' OR BB.facility_type = '02' THEN SUM(BB.capacity) END AS capacityPvWt ,SUM(BB.ess_capacity) AS ess_capacity FROM resource_set_list AA LEFT OUTER JOIN resource BB ON AA.resource_id = BB.id GROUP BY AA.resource_set_id) B ON A.id = B.resource_set_id LEFT OUTER JOIN forecast_by_resource_set C ON A.id = C.resource_set_id ) CC WHERE 1=1 AND CC.중개사업자ID = #{중개사업자ID}
AND CC.집합자원ID = #{id}
AND CC.집합자원구분 = #{type}
AND CC.운영상태 = #{status}
AND CC.집합자원그룹명 LIKE CONCAT('%',#{name},'%')
AND CC.신청유형 = #{apply_type}
AND CC.처리상태 = #{apply_status}
AND CC.오차율평가 >= 0 AND CC.오차율평가 <= 6
AND CC.오차율평가 > 6 AND CC.오차율평가 <= 8
AND CC.오차율평가 > 8

mapper.update:
   UPDATE event SET
       approved_memo = #{승인자메모}
WHERE id = #{이벤트ID}

mapper.select:
SELECT A.id AS 자원ID ,A.facility_type AS 설비구분 ,A.status AS 발전상태 ,A.capacity AS 설비용량 ,A.ess_status AS ESS상태 ,A.ess_capacity AS ESS용량 ,A.name AS 발전자원명 ,B.avg_error_rate AS 평균오차율 ,B.max_error_rate AS 최대오차율 ,B.min_error_rate AS 최소오차율 ,C.contract_end_date AS 계약종료일시 ,A.created_at AS 자원추가일시 FROM resource A LEFT OUTER JOIN ( SELECT resource_id ,(SUM(error_rate) / COUNT(error_rate)) *100 AS avg_error_rate ,MAX(error_rate) AS max_error_rate ,MIN(error_rate) AS min_error_rate FROM forecast_by_resource GROUP BY resource_id ) B ON A.id = B.resource_id LEFT OUTER JOIN resource_meta C ON A.id = C.resource_id WHERE A.id NOT IN(SELECT resource_id FROM resource_set_list)
AND A.facility_type = #{facility_type}
AND A.status = #{status}
AND A.name LIKE CONCAT('%',#{name},'%')
AND A.capacity >= #{stat_capacity}
AND A.capacity <= #{end_capacity}
AND B.avg_error_rate >= #{stat_avg_error_rate}
AND B.avg_error_rate <= #{end_avg_error_rate}
ORDER BY A.id LIMIT #{페이지번호},#{행갯수}

SELECT
   COUNT(A.id)  AS CNT
FROM resource A
         LEFT OUTER JOIN ( SELECT
                               resource_id
                                ,(SUM(error_rate) / COUNT(error_rate)) *100  AS avg_error_rate
                                ,MAX(error_rate) AS max_error_rate
                                ,MIN(error_rate) AS min_error_rate
                           FROM forecast_by_resource GROUP BY resource_id
) B ON  A.id = B.resource_id
        LEFT OUTER JOIN resource_meta C ON A.id = C.resource_id
WHERE A.id NOT IN(SELECT resource_id FROM resource_set_list)

    AND A.facility_type = #{facility_type}


    AND A.status = #{status}


    AND A.name LIKE CONCAT('%',#{name},'%')


    AND A.capacity  >=   #{stat_capacity}


    AND A.capacity  <=   #{end_capacity}


    AND B.avg_error_rate  >=   #{stat_avg_error_rate}


    AND B.avg_error_rate  <=   #{end_avg_error_rate}

ORDER BY A.id

