    ,business_registration_no = #{사업자등록번호}
    ,address = #{주소}
    ,ceo = #{대표자명}
    ,ceo_contract = #{대표자전화번호}
    ,manager = #{담당자명}
    ,manager_contract = #{당담자전화번호}
WHERE id = #{발전사업자ID}
